    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int a;
        int b;
        int c;
        try {

            if (// check if the fields are empty
                    (jTextField1.getText() != null && jTextField2.getText() != null && jTextField3.getText() != null)
                    && (!jTextField1.getText().equals("") && !jTextField2.getText().equals("") && !jTextField3.getText().equals(""))) {
                a = Integer.valueOf(jTextField1.getText());
                b = Integer.valueOf(jTextField2.getText());
                c = Integer.valueOf(jTextField3.getText());
                if (a != 0 && b != 0 && c != 0) {
                    if (Math.abs(a) == Math.abs(b) && Math.abs(b) == Math.abs(c)) {
                        jLabel2.setText("equilateral ");
                    } else if (Math.abs(a) == Math.abs(b) || Math.abs(b) == Math.abs(c) || Math.abs(c) == Math.abs(a)) {
                        jLabel2.setText("Isosceles");
                    } else {
                        jLabel2.setText("Scalene");
                    }
                } else {
                    jLabel2.setText("If one side or more \n can not be 0 long");
                }
            } else {
                jLabel2.setText("The fields can not be empty");

            }
        } catch (NumberFormatException ex) {//is the entry is not a number catch it :)
            jLabel2.setText(" is not a number !! \n Try again.");
        }
        return;

    }//GEN-LAST:event_jButton1ActionPerformed

